{
  "query": "query GetAllConsults {
    getAllConsults {
      id
      reason
      statusConsult
      date
      localTime
      patient {
        name
        email
      }
      nameProfessional
    }
  }"
}

{
  "query": "query GetFilteredConsults($filter: ConsultFilterRequestDto) {
    getFilteredConsults(filter: $filter) {
      id
      reason
      statusConsult
      date
      localTime
      patient {
        name
        email
      }
      nameProfessional
    }
  }",
  "variables": {
    "filter": {
      "patientEmail": "joao@email.com"
    }
  }
}

{
  "query": "query GetFilteredConsults($filter: ConsultFilterRequestDto) {
    getFilteredConsults(filter: $filter) {
      id
      reason
      statusConsult
      date
      localTime
      patient {
        name
        email
      }
      nameProfessional
    }
  }",
  "variables": {
    "filter": {
      "patientEmail": "joao@email.com",
      "status": "SCHEDULED"
    }
  }
}

{
  "query": "mutation CreateFullConsult($input: ConsultRequestDto!) {\n    createFullConsult(input: $input) {\n      id\n      reason\n      statusConsult\n      date\n      localTime\n      patient {\n        name\n        email\n      }\n      nameProfessional\n    }\n  }",
  "variables": {
    "input": {
      "reason": "Consulta de rotina",
      "date": "2025-10-15",
      "localTime": "14:30:00",
      "patient": {
        "name": "Jo√£o Silva",
        "email": "joao@email.com"
      },
      "professional": {
        "name": "Dr. Silva",
        "email": "dr.silva@email.com"
      }
    }
  }
}

{
  "query": "mutation UpdateConsult($input: ConsultUpdateRequestDto!) {\n    updateConsult(input: $input) {\n      id\n      reason\n      statusConsult\n      date\n      localTime\n      nameProfessional\n    }\n  }",
  "variables": {
    "input": {
      "id": "1",
      "reason": "Consulta de retorno - ATUALIZADA",
      "date": "2025-10-20",
      "localTime": "15:00:00",
      "status": "CARRIED_OUT",
      "professional": {
        "name": "Dr. Silva",
        "email": "dr.silva@email.com"
      }
    }
  }
}

{
  "query": "mutation {
    createFullConsult(input: {
      reason: "Consulta de teste - Fluxo completo"
      date: "2025-11-01"
      localTime: "10:00:00"
      patient: {
        name: "Jo√£o Silva"
        email: "joao@email.com"
      }
      professional: {
        name: "Dr. Silva"
        email: "dr.silva@email.com"
      }
    }) {
      id
      reason
      statusConsult
      date
      localTime
    }
  }"
}

{
  "query": "query {
    getAllConsults {
      id
      reason
      statusConsult
      date
      localTime
    }
  }"
}

{
  "query": "mutation {
    updateConsult(input: {
      id: "{{consult_id}}"
      reason: "Consulta ATUALIZADA - Fluxo completo"
      date: "2025-11-02"
      localTime: "11:00:00"
      status: CARRIED_OUT
      professional: {
        name: "Dr. Silva"
        email: "dr.silva@email.com"
      }
    }) {
      id
      reason
      statusConsult
      date
      localTime
    }
  }"
}

{
  "query": "query {
    getFilteredConsults(filter: {status: SCHEDULED}) {
      id
      reason
      statusConsult
      date
      localTime
    }
  }"
}

{
  "query": "query {
    getFilteredConsults(filter: {status: CARRIED_OUT}) {
      id
