    }
  }"
}

{
  "query": "query {
    getFilteredConsults(filter: {patientEmail: "joao@email.com"}) {
      id
      reason
      statusConsult
      patient {
        name
        email
      }
    }
  }"
}

{
  "query": "query IntrospectionQuery {
    __schema {
      queryType { name }
      mutationType { name }
      types {
        ...FullType
      }
    }
  }

  fragment FullType on __Type {
    kind
    name
    description
    fields(includeDeprecated: true) {
      name
      description
      args {
        ...InputValue
      }
      type {
        ...TypeRef
      }
    }
  }

  fragment InputValue on __InputValue {
    name
    description
    type { ...TypeRef }
    defaultValue
  }

  fragment TypeRef on __Type {
    kind
    name
    ofType {
      kind
      name
      ofType {
        kind
        name
      }
    }
  }"
}
